import React, { useState } from "react";
import { Form } from "react-bootstrap";
const COLORS = [
    "red",
    "blue",
    "green",
    "orange",
    "purple",
    "cyan",
    "magenta",
    "white"
];

export function ChangeColor(): JSX.Element {
    const [color, setColor] = useState<string>("");
    const colorChoice = document.createElement("div");
    function updateColor(event: React.ChangeEvent<HTMLInputElement>) {
        setColor(event.target.value);
        colorChoice.innerHTML = color;
    }
    return (
        <div>
            {COLORS.map((colorValue: string) => (
                <Form.Check key={colorValue}>
                    <Form.Check
                        type="radio"
                        name="colors"
                        onChange={updateColor}
                        id={"color-check" + colorValue}
                        label={color}
                        value={colorValue}
                        checked={color === colorValue}
                    />
                </Form.Check>
            ))}
            <p>
                <span>You have chosen </span>
                <span data-testid="colored-box">{colorChoice}</span>;
            </p>
        </div>
    );
}
